import React from 'react';
import {Col, Container, Image, Row} from "react-bootstrap";
import BigStar128 from '../assets/BigStar128.png'
import BigStar256 from '../assets/BigStar256.png'

const DevicePage = () => {
    const device = {id: 1, name: 'Iphone 12', price: 25000, rating: 5, img: 'https://s.62.ua/section/promonewsintext/upload/images/promo/intext/000/052/773/wwruiphone12q121blackpdp-image-1b_5fb284836ddb9.jpg'}

        return (
        <Container className={"mt-3"}>
            <Col md={4}>
                <Image width={300} height={300} src={device.img}/>
            </Col>
            <Col md={4}>
                <Row className="d-flex flex-column align-items-center">
                    <h2>{device.name}</h2>
                    <div
                        className="d-flex align-items-center justify-content-center"
                        style={{background: `url(${BigStar256}) no-repeat center center`, width: 256, height: 256, backgroundSize: 'cover', fontSize: 64}}
                    >
                        {device.rating}
                    </div>
                </Row>
            </Col>

            <Col md={4}>

            </Col>
        </Container>
    );
};

export default DevicePage;
