import React from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";
import BigStar256 from '../assets/BigStar256.png'

const DevicePage = () => {
    const device = {
        id: 1,
        name: 'Iphone 12',
        price: 25000,
        rating: 5,
        img: 'https://s.62.ua/section/promonewsintext/upload/images/promo/intext/000/052/773/wwruiphone12q121blackpdp-image-1b_5fb284836ddb9.jpg'
    }
    const description = [
        {id:1, title: 'Оперативная память', description: '4 гб'},
        {id:2, title: 'Камера', description: '12 мп'},
        {id:3, title: 'Процессор', description: 'A13 Bionic'},
        {id:4, title: 'Количество ядер', description: '8'},
        {id:5, title: 'Аккумулятор', description: '3000мАч'},
        {id:6, title: 'Вес', description: '187 гр'},
        {id:7, title: 'Гарантия', description: '1 год'},
    ]
    return (
        <Container className={"mt-3"}>
            <Row>
                <Col md={4}>
                    <Image width={300} height={300} src={device.img}/>
                </Col>
                <Col md={4}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2>{device.name}</h2>
                        <div
                            className="d-flex align-items-center justify-content-center"
                            style={{
                                background: `url(${BigStar256}) no-repeat center center`,
                                width: 256,
                                height: 256,
                                backgroundSize: 'cover',
                                fontSize: 64
                            }}
                        >
                            {device.rating}
                        </div>
                    </Row>
                </Col>

                <Col md={4}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{width:300, height:300, fontSize:32, border: '5px solid lightgray'}}
                    >
                        <h3>От: {device.price} тенге</h3>
                        <Button variant={"outline-dark"}>Добавить в корзину</Button>
                    </Card>
                </Col>
                <Row className="d-flex flex-column mt-3">
                    {description.map((info, index) =>
                    <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10}}>
                        {info.title}: {info.description}
                    </Row>
                    )}
                </Row>
            </Row>
        </Container>
    );
};

export default DevicePage;
