import {makeAutoObservable} from "mobx";

export default class DeviceStore {
    constructor() {
        this._types = [
            {id: 1, name: 'Холодильник'},
            {id: 2, name: 'Смартфоны'},
            {id: 3, name: 'Телевизоры'},
            {id: 4, name: 'Ноутбуки'},
        ]
        this._brands = [
            {id: 1, name: 'Samsung'},
            {id: 2, name: 'Apple'},
            {id: 3, name: 'Huawei'},
            {id: 4, name: 'Oppo'},

        ]
        this._devices = [
            {id: 1, name: 'Iphone 13 pro', price: 25000, rating: 5, img: 'https://www.technodom.kz/media/catalog/product/cache/1366e688ed42c99cd6439df4031862c6/7/2/72b8ecc5b27b926fd24598f4c6a2ba36728abdf6_228632_1.jpg'},
            {id: 2, name: 'Iphone 13 pro', price: 25000, rating: 5, img: 'https://www.technodom.kz/media/catalog/product/cache/1366e688ed42c99cd6439df4031862c6/7/2/72b8ecc5b27b926fd24598f4c6a2ba36728abdf6_228632_1.jpg'},
            {id: 3, name: 'Iphone 13 pro', price: 25000, rating: 5, img: 'https://www.technodom.kz/media/catalog/product/cache/1366e688ed42c99cd6439df4031862c6/7/2/72b8ecc5b27b926fd24598f4c6a2ba36728abdf6_228632_1.jpg'},
            {id: 4, name: 'Iphone 13 pro', price: 25000, rating: 5, img: 'https://www.technodom.kz/media/catalog/product/cache/1366e688ed42c99cd6439df4031862c6/7/2/72b8ecc5b27b926fd24598f4c6a2ba36728abdf6_228632_1.jpg'},
         ]
        this._selectedType = {}
        this._selectedBrand = {}
       this._selectedDevice = {}
        makeAutoObservable(this)
    }

    setTypes(types) {
        this._types = types
    }
    setBrands(brands) {
        this._brands = brands
    }
    setDevices(devices) {
        this._devices = devices
    }
    setSelectedType(type) {
        this._selectedType = type
    }
    setSelectedBrand(brand) {
        this._selectedBrand = brand
    }
    setSelectedDevice(device) {
        this._selectedDevice = device
    }

    get types(){
        return this._types
    }
    get brands() {
        return this._brands
    }
    get devices() {
        return this._devices
    }
    get selectedType() {
        return this._selectedType
    }
    get selectedBrand() {
        return this._selectedBrand
    }

    get selectedDevice() {
        return this._selectedDevice
    }
     
}
