import {makeAutoObservable} from "mobx";

export default class DeviceStore {
    constructor() {
        this._types = [
            {id: 1, name: 'Холодильник'},
            {id: 2, name: 'Смартфоны'},
            {id: 3, name: 'Телевизоры'},
            {id: 4, name: 'Ноутбуки'},
        ]
        this._brands = [
            {id: 1, name: 'Samsung'},
            {id: 2, name: 'Apple'},
            {id: 3, name: 'Huawei'},
            {id: 4, name: 'Oppo'},

        ]
        this._devices = [
            {id: 1, name: 'Iphone 13 pro', price: 25000, rating: 5,},
            {id: 3, name: 'Iphone 13 pro', price: 25000, rating: 5, img: 'https://images.app.goo.gl/jrXcKzUAbMzBVsBaA'},
