            {id: 5, name: 'Iphone 13 pro', price: 25000, rating: 5, img: 'https://images.app.goo.gl/jrXcKzUAbMzBVsBaA'},
            {id: 6, name: 'Iphone 13 pro', price: 25000, rating: 5, img: 'https://images.app.goo.gl/jrXcKzUAbMzBVsBaA'},
            {id: 7, name: 'Iphone 13 pro', price: 25000, rating: 5, img: 'https://images.app.goo.gl/jrXcKzUAbMzBVsBaA'},
            {id: 8, name: 'Iphone 13 pro', price: 25000, rating: 5, img: 'https://images.app.goo.gl/jrXcKzUAbMzBVsBaA'},

        ]
        this._selectedType = {}
        this._selectedBrand = {}
        makeAutoObservable(this)
    }

    setTypes(types) {
        this._types = types
    }
    setBrands(brands) {
        this._brands = brands
    }
    setDevices(devices) {
        this._devices = devices
    }
    setSelectedType(type) {
        this._selectedType = type
    }
    setSelectedBrand(brand) {
        this._selectedBrand = brand
    }
    setSelectedDevice(device) {
        this._selectedDevice = device
    }

    get types(){
        return this._types
    }
    get brands() {
        return this._brands
    }
    get devices() {
        return this._devices
    }
    get selectedType() {
        return this._selectedType
    }
    get selectedBrand() {
        return this._selectedBrand
    }
    get selectedDevice() {
        return this._selectedDevice
    }
}
